import React,{Component} from 'react'
import classes from './Maze.module.css'
import Cell from '../Cell/Cell'



class Maze extends Component
{

    constructor(props)
    {
        super(props)
        let grid=new Array(3)
        for(let i=0;i<3;i++)
          grid[i]=new Array(3)

          for(let i=0;i<3;i++)
          {
              for(let j=0;j<3;j++)
               grid[i][j]=0;  
          }

          //1 denotes constraint
          grid[1][0]=1




          //-1 is start
          grid[0][0]=-1

          //3 denote end
          grid[2][2]=3

          //2 denotes valid answer


        this.state={
            grid:grid,
            solutions:[]
        }
    }

    isSafe(i,j,matrix)
    {
        return i>=0 && j>=0 && i<3 && j<3 && matrix[i][j]===0 && this.state.grid[i][j]!==1
    }


    recurse(i,j,matrix,ans)
    {

        if(i===2 && j===2 )
        {
            ans.push(matrix.toString())
            return true
        }

        let flag=0;
        if(this.isSafe(i-1,j,matrix))
        {
            matrix[i-1][j]=1
            flag|=this.recurse(i-1,j,matrix,ans)
            matrix[i-1][j]=0
        }
        if(this.isSafe(i+1,j,matrix))
        {
            matrix[i+1][j]=1
            flag|=this.recurse(i+1,j,matrix,ans)
            matrix[i+1][j]=0
        }
        if(this.isSafe(i,j-1,matrix))
        {
            matrix[i][j-1]=1
            flag|=this.recurse(i,j-1,matrix,ans)
            matrix[i][j-1]=0
        }
        if(this.isSafe(i,j+1,matrix))
        {
            //console.log(matrix.toString(),'a',i,j)
            matrix[i][j+1]=1
            //console.log(matrix.toString(),'b',i,j)
            flag|=this.recurse(i,j+1,matrix,ans)
            matrix[i][j+1]=0
        }

        return flag;
    }

    render()
    {

        let grid=new Array(3)
        for(let i=0;i<3;i++)
          grid[i]=new Array(3)

          for(let i=0;i<3;i++)
          {
              for(let j=0;j<3;j++)
               grid[i][j]=0;  
          }

         let ans=[]
         this.recurse(0,0,grid,ans)
         console.log(ans)



        return (
            <div className={classes.gridContainer}>
                {this.state.grid.map(g=>{
                   return (
                       g.map(gc=>{
                           return <Cell value={gc}></Cell>
                       })
                   )}
                )}

            </div>
        )
    }

}

export default Maze
