import React,{Component} from 'react'
import classes from './Maze.module.css'
import Cell from '../Cell/Cell'



class Maze extends Component
{

    constructor(props)
    {
        super(props)
        let grid=new Array(3)
        for(let i=0;i<3;i++)
          grid[i]=new Array(3)

          for(let i=0;i<3;i++)
          {
              for(let j=0;j<3;j++)
               grid[i][j]=0;  
          }

          //1 denotes constraint
          grid[1][0]=1
          grid[1][1]=1



          //-1 is start
          grid[0][0]=-1

          //3 denote end
          grid[2][2]=3

          //2 denotes valid answer


        this.state={
            grid:grid,
            solutions:[]
        }
    }

    isSafe(i,j,matrix)
    {
        return i>=0 && j>=0 && i<3 && j<3 && matrix[i][j]===0
    }

    render()
    {
        console.log(this.isSafe(2,2,this.state.grid[2][2]))

        return (
            <div className={classes.gridContainer}>
                {this.state.grid.map(g=>{
                   return (
                       g.map(gc=>{
                           return <Cell value={gc}></Cell>
                       })
                   )}
                )}

            </div>
        )
    }

}

export default Maze
