import React, { Component } from 'react'
import classes from './Form.module.css'


class Form extends Component
{
    constructor(props)
    {
          super(props)
          this.state={
              value:''
          }
    }

    onChangeHandler=(val)=>{
        this.setState({value:val})
    }

    onSubmitHandler=(e)=>{
        e.preventDefault()
        this.props.submit(this.state.value)
    }

    render()
    {
        return (
             <form>
                 <label for="value">Enter no. of cells you want to have in grid</label>
                 <input type="text" value={this.state.value} onChange={this.state.onChangeHandler} placeholder={3}></input>
             </form>
        )
    }

}

export default Form
